<script>

import anime from "animejs";
import * as d3 from "d3";

export default {
	name: 'KitMainModulesOrder',

	props: {
		typeKitchen: {
			type: String,
			default: 'straight'
		},

		fridge: {
			type: String,
			default: 'no'
		}
	},

	data () {
		return {
			isDraggableSink: false,
			isDraggableOven: false,
			sinkX: null,
			ovenX: null,
			sinkXNext: null,
			ovenXNext: null,
			fridge_x: null,
			dragOffsetX: 0,
			leftWall: [],
			rightWall: []
		}
	},

	computed: {
		moveAnimation () {
			const duration = 200
			const easing = 'linear'
			return anime({
				autoplay: false,
				duration,
				targets: {
					sink: this.sinkX,
					oven: this.ovenX
				},
				sink: this.sinkXNext,
				oven: this.ovenXNext,
				easing,
				update: (data) => {
					this.sinkX = data.animations[0].currentValue
					this.ovenX = data.animations[1].currentValue
				}
			})
		},
		hoverAnimation () {
			const hoveredScale = 1.2
			const normalScale = 1
			const hoveredTranslateX = -20
			const normalTranslateX = 0
			const tl = anime.timeline({
				easing: 'linear',
				update () {
				}
			})
			tl.add({
				targets: this.$refs.sink,
				scale: this.isDraggableSink ? hoveredScale : normalScale,
				translateX: this.isDraggableSink ? hoveredTranslateX : normalTranslateX,
				duration: this.isDraggableSink ? 50 : 100,
				easing: 'linear'
			})
			tl.add({
				targets: this.$refs.oven,
				scale: this.isDraggableOven ? hoveredScale : normalScale,
				translateX: this.isDraggableOven ? hoveredTranslateX : normalTranslateX,
				duration: this.isDraggableOven ? 50 : 100,
				easing: 'linear',
				offset: -200
			})
			return tl
		},

		fridgeWidth () {
			return 47
		},
		ovenWidth () {
			return 66
		},
		sinkWidth () {
			return 62
		},
		fridgeSegmentPadding () {
			return 8
		},
		fridgeSegmentSize () {
			return this.fridgeWidth + this.fridgeSegmentPadding * 2
		},
		fridgeXStart () {
			return this.fridgeWidth / 2
		},
		fridgeXEnd () {
			return this.segmentsEnd - this.fridgeSegmentPadding
		},
		sinkOffsetX () {
			return this.segmentWidth / 2 - this.sinkWidth / 2
		},
		ovenOffsetX () {
			return this.segmentWidth / 2 - this.ovenWidth / 2
		},
		boxWidth () {
			return 500
		},
		segmentsTotalWidth () {
			return this.boxWidth - (this.fridgeSegmentSize * 2)
		},
		segmentsStart () {
			return this.fridgeSegmentSize
		},
		segmentsEnd () {
			return this.boxWidth - this.fridgeSegmentSize
		},
		segmentsTotalPerWall () {
			return this.typeKitchen === 'corner' ? 2 : 4
		},
		wallsTotals () {
			return this.typeKitchen === 'corner' ? 2 : 1
		},
		segmentsTotal () {
			return this.segmentsTotalPerWall * this.wallsTotals
		},
		segmentWidth () {
			return this.segmentsTotalWidth / this.segmentsTotal
		},
		walls () {
			const wallLength = this.segmentWidth * this.segmentsTotalPerWall
			const leftStart = this.segmentsStart
			const leftEnd = leftStart + wallLength
			const rightStart = leftEnd
			const rightEnd = this.segmentsEnd

			const left = {
				start: leftStart,
				end: leftEnd,
				segments: Array(this.segmentsTotalPerWall).fill().map((el, i) => {
					const start = i * this.segmentWidth + leftStart
					const end = start + this.segmentWidth
					return { start, end }
				})
			}

			let rightSegments = []
			if (this.typeKitchen === 'corner') {
				rightSegments = Array(this.segmentsTotalPerWall).fill().map((el, i) => {
					const start = i * this.segmentWidth + rightStart
					const end = start + this.segmentWidth
					return { start, end }
				})
			}
			const right = {
				start: rightStart,
				end: rightEnd,
				segments: rightSegments
			}

			return {
				left,
				right
			}
		},
		sinkSegment () {
			return this.findSegmentByX(this.sinkX)
		},
		ovenSegment () {
			return this.findSegmentByX(this.ovenX)
		},
		firstSegment () {
			return this.findSegmentByX(this.segmentsStart)
		},
		lastSegment () {
			return this.findSegmentByX(this.segmentsEnd)
		},
		percentStove: {
			get () {
				return localStorage.getItem('percent_stove') ?? null
			},
			set (newValue) {
				localStorage.setItem('percent_stove', newValue)
			}
		},
		percentWasher: {
			get () {
				return localStorage.getItem('percent_washer') ?? null
			},
			set (newValue) {
				localStorage.setItem('percent_washer', newValue)
			}
		},
		stoveOnLeftWall: {
			get () {
				return localStorage.getItem('stove_on_left_wall') ?? true
			},
			set (newValue) {
				localStorage.setItem('stove_on_left_wall', newValue)
			}
		},
		washingOnLeftWall: {
			get () {
				return localStorage.getItem('washing_on_left_wall') ?? true
			},
			set (newValue) {
				localStorage.setItem('washing_on_left_wall', newValue)
			}
		},
		sinkXComputed () {
			let segmentIndex = 0
			const wall = this.washingOnLeftWall ? 'left' : 'right'
			const indexSearchStrategies = {
				left: () => this.segmentsTotalPerWall - Math.ceil(this.percentWasher * this.segmentsTotalPerWall / 100),
				right: () => Math.ceil(this.percentWasher * this.segmentsTotalPerWall / 100) - 1
			}
			if (this.percentWasher !== null) {
				segmentIndex = indexSearchStrategies[wall]()
			}
			return this.walls[wall].segments[segmentIndex].start + this.sinkOffsetX
		},
		ovenXComputed () {
			let segmentIndex = 1
			const wall = this.stoveOnLeftWall ? 'left' : 'right'
			const indexSearchStrategies = {
				left: () => this.segmentsTotalPerWall - Math.ceil(this.percentStove * this.segmentsTotalPerWall / 100),
				right: () => Math.ceil(this.percentStove * this.segmentsTotalPerWall / 100) - 1
			}
			if (this.percentStove !== null) {
				segmentIndex = indexSearchStrategies[wall]()
			}
			return this.walls[wall].segments[segmentIndex].start + this.ovenOffsetX
		}
	},

	watch: {
		typeKitchen () {
			this.resetScene()
		},
		fridge () {
			this.resetScene()
		}
	},

	created () {
		this.resetScene()
	},

	methods: {
		resetScene () {
			// Place sink after oven in the beginning of scene. And show fridge if selected
			localStorage.clear()
			this.sinkX = this.sinkXComputed
			this.ovenX = this.ovenXComputed
			this.fridge_x = this.fridge === 'left' ? this.fridgeXStart : this.fridgeXEnd
		},

		changeZOrder (el) {
			const sel = d3.selectAll('use')
			const indexOfUpperElement = sel.nodes().findIndex(item => item.id === el)
			const array = sel.nodes().map((item, index) => {
				return index
			})
			const zOrder = []
			array.slice(0, indexOfUpperElement).forEach(item => zOrder.push(item))
			array.slice(indexOfUpperElement + 1).forEach(item => zOrder.push(item))
			zOrder.push(indexOfUpperElement)
			const setOrder = d3.ascending
			sel.data(zOrder)
			sel.sort(setOrder)
		},

		/**
		 * Find segment that contains position X
		 * @return: segment's object {wall: 'right' || 'left', segment {start: Number, end: Number}}
		 */
		findSegmentByX (positionX) {
			positionX = positionX || 0

			if (positionX < this.segmentsStart) {
				positionX = this.segmentsStart
			}
			if (positionX > this.segmentsEnd) {
				positionX = this.segmentsEnd
			}

			const wallKey = Object.keys(this.walls).find((key) => {
				return positionX >= this.walls[key].start && positionX <= this.walls[key].end
			})

			const segment = this.walls[wallKey].segments.find((seg) => {
				return positionX >= seg.start && positionX <= seg.end
			})
			return {
				wall: wallKey,
				segment
			}
		},

		findPercentOfElement () {
			const segmentOvenByX = this.findSegmentByX(this.ovenXNext)
			const indexOfOvenSegment = this.walls[segmentOvenByX.wall].segments.indexOf(segmentOvenByX.segment)
			if (segmentOvenByX.wall === 'left') {
				this.percentStove = Math.floor(100 - 100 / this.segmentsTotalPerWall * indexOfOvenSegment)
				this.stoveOnLeftWall = true
			} else {
				this.percentStove = Math.floor(100 / this.segmentsTotalPerWall * (indexOfOvenSegment + 1))
				this.stoveOnLeftWall = false
			}

			const segmentSinkByX = this.findSegmentByX(this.sinkXNext)
			const indexOfSinkSegment = this.walls[segmentSinkByX.wall].segments.indexOf(segmentSinkByX.segment)
			this.percentWasher = 100 - 100 / this.segmentsTotalPerWall * indexOfSinkSegment
			if (segmentSinkByX.wall === 'left') {
				this.percentWasher = Math.floor(100 - 100 / this.segmentsTotalPerWall * indexOfSinkSegment)
				this.washingOnLeftWall = true
			} else {
				this.percentWasher = Math.floor(100 / this.segmentsTotalPerWall * (indexOfSinkSegment + 1))
				this.washingOnLeftWall = false
			}
		},

		/**
		 * Find where to move possible occupant from a segment (newSegment)
		 * In respect to oldSegment it would be left (-1) or right (1)
		 * @return: -1 (i.e. move occupant to left) or 1 (i.e. move occupant to right)
		 */
		getOccupantMoveMultiplier (oldSegment, newSegment) {
			// lets sink where we would move another module in case of collision in nonvacant segment
			const isMoveToLeft = oldSegment.segment.start > newSegment.segment.start
			let moveMultiplier = isMoveToLeft ? 1 : -1
			if (newSegment.segment === this.lastSegment.segment) {
				// if this is a last segment move left
				moveMultiplier = -1
			}
			if (newSegment.segment === this.firstSegment.segment) {
				// if this is a last segment move right
				moveMultiplier = 1
			}
			return moveMultiplier
		},

		/**
		 * Place a sink into segment respective to position X
		 */
		dropSinkToX (positionX) {
			// gonna find new segment for this module
			const match = this.findSegmentByX(positionX)
			let ovenNewPositionX = null

			// lets think where we would move another module in case of collision in nonvacant segment
			const occupantMoveMultiplier = this.getOccupantMoveMultiplier(this.sinkSegment, match)

			if (this.ovenSegment.segment === match.segment) {
				/*
				 * Modules collision occured.
				 * To make a vacant place for this module,
				 * lets move occupant left or right in respect ot swipe direction
				 */
				ovenNewPositionX = this.ovenX + (this.segmentWidth * occupantMoveMultiplier)
			}

			if (match) {
				this.moveAnimation.pause()
				this.sinkXNext = match.segment.start + this.sinkOffsetX
				this.ovenXNext = ovenNewPositionX === null ? this.ovenX : ovenNewPositionX
				this.moveAnimation.play()
				this.findPercentOfElement()
			}
		},

		/**
		 * Place an oven into segment respective to position X
		 */
		dropOvenToX (positionX) {
			// gonna find new segment for this module
			const match = this.findSegmentByX(positionX)
			let sinkNewPositionX = null

			// lets think where we would move another module in case of collision in nonvacant segment
			const occupantMoveMultiplier = this.getOccupantMoveMultiplier(this.ovenSegment, match)

			if (this.sinkSegment.segment === match.segment) {
				/*
				 * Modules collision occured.
				 * To make a vacant place for this module,
				 * lets move occupant left or right in respect ot swipe direction
				 */
				sinkNewPositionX = this.sinkX + (this.segmentWidth * occupantMoveMultiplier)
			}
			if (match) {
				this.moveAnimation.pause()
				this.ovenXNext = match.segment.start + this.ovenOffsetX
				this.sinkXNext = sinkNewPositionX === null ? this.sinkX : sinkNewPositionX
				this.moveAnimation.play()
				this.findPercentOfElement()
			}
		},

		// /**
		//  * Convert move event to X position
		//  * @return: number
		//  */
		// moveEventToX (event) {
		// 	const moduleWidth = event.target.getBoundingClientRect().width
		// 	const offset = event.target.parentElement.getBoundingClientRect().x + moduleWidth / 2
		// 	return event.touchmoveX - offset
		// },

		dragStartSink () {
			this.isDraggableSink = true
			this.isDraggableOven = false
			this.hoverAnimation.play()
		},
		dragSink (event) {
			const moduleWidth = this.$refs.sink.getBoundingClientRect().width
			if (this.isDraggableSink) {
				switch (event.pointerType) {
					case 'mouse':
						this.sinkX = event.offsetX - moduleWidth
						break
					case 'touch':
						this.sinkX = event.offsetX + moduleWidth
						break
				}
			}
		},

		dragStartOven () {
			this.isDraggableOven = true
			this.isDraggableSink = false
			this.hoverAnimation.play()
		},
		dragOven (event) {
			const moduleWidth = this.$refs.oven.getBoundingClientRect().width
			if (this.isDraggableOven) {
				switch (event.pointerType) {
					case 'mouse':
						this.ovenX = event.offsetX - moduleWidth
						break
					case 'touch':
						this.ovenX = event.offsetX + moduleWidth
						break
				}
			}
		},

		dragModule (event) {
			if (this.isDraggableOven) {
				this.changeZOrder('oven__copy')
				this.dragOven(event)
			}
			if (this.isDraggableSink) {
				this.changeZOrder('sink__copy')
				this.dragSink(event)
			}
		},
		dragEndModule () {
			if (this.isDraggableOven) {
				this.dropOvenToX(this.ovenX)
			}
			if (this.isDraggableSink) {
				this.dropSinkToX(this.sinkX)
			}
			this.isDraggableOven = false
			this.isDraggableSink = false
			this.hoverAnimation.play()
		}
	}
}

